//we used the chart js here to display our data to learn more goto chart.js official website 

import React from 'react'
import {Chart as ChartJs , 
       CategoryScale,
       LinearScale,
       PointElement,
       LineElement,
       Title,
       Tooltip,
       Legend,
       ArcElement,

} from 'chart.js'

import {Line} from 'react-chartjs-2'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'

ChartJs.register(
    CategeoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    tooltip,
    legend,
    ArcElement,
)

function Chart() {
 const {incomes ,expenses} = useGlobalContext()

 const data = {
    labels: incomes.map((inc) =>{
        const {date} = inc
        return (date)
    }),
    datasets: [
        {
            label: 'Income',
            data: [
                ...incomes.map((income) => {
                    // we toke amont from income 
                    const {amount} = income 
                    return amount

                })
            ],
            backgroundColor:'blue'
        },
        {
            label: 'Expenses',
            data: [
                ...expenses.map((expense) => {
                    // we toke amont from income 
                    const {amount} = expense 
                    return amount

                })
            ],
            backgroundColor:'red'
        }
    ]
 }

  return (
    <ChartStyled>
       < Line data={data}/>
    </ChartStyled>
  )
}
const ChartStyled = styled.div `
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

export default Chart
